import React, { useEffect, useRef, useState } from "react";
import {Card, Row, Col, Form,Button,Table, FormControl, Modal} from "react-bootstrap";

export default function Payment(){

    const[checkout, setCheckOut] = useState(false)

    const paypal = useRef()

    useEffect(()=>{
        window.paypal.Buttons({
            createOrder: (data,actions,err) =>{
                return actions.order.create({
                    intent:"CAPTURE",
                    purchase_units: [
                        {
                            description:"Thanh toan ve xe",
                            amount:{
                                currency_code:"CAD",
                                value: 650.00
                            }
                        }
                    ]

                })
            },
            onApprove: async (data, actions) =>{
                const order = await actions.order.capture()
                console.log(order)
            },
            onError: (err) =>{
                console.log(err)
            }
        }).render(paypal.current)

    },[])


    return(
        <>
            
            {checkout ?(
                <div>
                    <div ref={paypal}></div>
                </div>

            ):(
                <Button onClick={()=>{setCheckOut(true)}} style={{backgroundColor:"rgb(30, 105, 175)"}} type="submit" variant="primary">
                    Thanh to√°n
                </Button>
            )}
        </>
    )
}
