import {Card, Row, Col, Form,Button} from "react-bootstrap";
import "./Search.css"
import Apis, { endpoints } from "../configs/Apis";
import React, { useState, useEffect } from 'react';

function Search() {

    const [points, setPoints] = useState([])

    useEffect(() => {
        const loadPoint = async ()=>{
            let res = await Apis.get(endpoints['lines'])
            setPoints(res.data)
        }
        loadPoint()
    })

    return (
        <Card>
            {points.map(c => <h1>{c.id}</h1>)}
            <Card.Body>
                <Row>
                    <Form style={{display:"flex"}}>
                        <Col>
                            <Form.Group className="search-form-group" controlId="exampleForm.ControlInput1">
                                <Form.Label style={{fontWeight:"bold", color:"rgb(98 121 142)", marginBottom:"1.5rem"}}>Chọn điểm khởi hành</Form.Label>
                                <Form.Select aria-label="Default select example">
                                    {points.map(c => <option>{c.name}</option>)} 
                                </Form.Select>
                            </Form.Group>
                            
                        </Col>
                        <Col>
                            <Form.Group className="search-form-group" controlId="exampleForm.ControlInput1">
                                <Form.Label style={{fontWeight:"bold", color:"rgb(98 121 142)", marginBottom:"1.5rem"}}>Chọn điểm đến</Form.Label>
                                <Form.Select aria-label="Default select example">
                                    <option>Chọn điểm đến</option>
                                    <option value="1">One</option>
                                    <option value="2">Two</option>
                                    <option value="3">Three</option>
                                </Form.Select>
                            </Form.Group>
                        </Col>
                        <button class="btn btn-primary btn-lg search-form-button" size="lg">
                            Tìm kiếm
                        </button>
                        
                    </Form>
                    
                </Row>
            </Card.Body>
        </Card>
    )
}

export default Search
