import { Link } from 'react-router-dom';
import {Card, Row, Col, Form,Button,Table, FormControl, Modal} from "react-bootstrap";
import React, { useState, useEffect } from 'react';
import Apis, { endpoints } from '../configs/Apis';
import cookies from "react-cookies"
import './TicketDetail.css';

export default function TicketDetail(props){
    const [detail, setDetail] = useState([])

    useEffect(() => {
        const loadDetail = async ()=>{
            let res = await Apis.get(endpoints["ticket-detail"](props.obj.id),{
                headers:{
                    'Authorization': `Bearer ${cookies.load("access_token")}`
                }
            })
            setDetail(res.data)
        }
        loadDetail()
    },[])

    console.log(detail)


    return(
        <Card>
            <Card.Body style={{border:"3px solid rgb(210, 225, 239)"}}>
                <Row>
                    <Col md={3} style={{textAlign:"center", borderRight:"3px dotted rgb(210, 225, 239)"}}>
                        <span style={{fontWeight:"bold"}}>
                            GHẾ
                        </span>
                        <h2 style={{marginBottom:"0", fontSize:"3rem", color:"rgb(30, 105, 175)"}}>
                            {detail.seat_position}
                        </h2>
                    </Col>
                    <Col md={7}>
                        <div>
                            <span style={{fontWeight:"bold"}}>
                                Mã vé: {props.obj.id}
                            </span>
                        </div>
                        

                    </Col>
                    <Col md={2}>
                    </Col>
                </Row>
            </Card.Body>
        </Card>
    )
}
