import {Card, Row, Col, Form,Button} from "react-bootstrap";
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function SignIn(){
    const [username, setUsername] = useState('')
    const [pass, setPass] = useState('')

    const onChangeUserName = (event) =>{
        const value = event.target.value
        setUsername(value)
    }

    const onChangePass = (event) =>{
        const value = event.target.value
        setPass(value)
    }
    const onSubmitLogin =()=>{

    }

    return(
        <>
            <div className="justify-content-md-center">
                <Card>
                    <Card.Body>
                        <h2 style={{textTransform:"uppercase", fontWeight:"bold"}}>Đăng nhập</h2>
                        <Form>
                            <Form.Group className="mb-3" controlId="Email">
                                <Form.Label>Tên đăng nhập</Form.Label>
                                <Form.Control 
                                type="email" 
                                placeholder="Tên đăng nhập..." 
                                onChange={onChangeUserName}
                                />
                                {/* <Form.Text className="text-muted">
                                    We'll never share your email with anyone else.
                                </Form.Text> */}
                                <p className="text-danger">111111111</p>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Mật khẩu</Form.Label>
                                <Form.Control 
                                type="password" 
                                placeholder="Mật khẩu..." 
                                onChange={onChangePass}
                                />
                                
                            </Form.Group>
                            <Form.Group style={{marginBottom: "1.5rem"}}>
                                Đã có tài khoản? <Link to={"/sign-up"}>Đăng ký tại đây</Link>
                            </Form.Group>
                            <div className="d-grid gap-2">
                                <button 
                                className="btn btn-secondary btn-lg" 
                                style={{background:"linear-gradient(90deg, rgb(119, 199, 253) 0%, rgb(26, 23, 23) 100%)"}} 
                                variant="secondary" 
                                size="lg"
                                onClick={onSubmitLogin}
                                >
                                    Đăng nhập
                                </button>
                            </div>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        </>
    )
}
